function Locations() {
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState(null);

  const handleImageClick = (country) => {
    setSelectedCountry(country);
    setIsDrawerOpen(true);
  };

  const closeDrawer = () => {
    setIsDrawerOpen(false);
    setSelectedCountry(null);
  };

  useEffect(() => {
    document.body.style.overflow = isDrawerOpen ? 'hidden' : 'auto';
  }, [isDrawerOpen]);

  return (
    <section className="locations" id="locations">
      <div className="package-titlelocaion">
        <h1 style={{ color: "black" }}>Explore Breathtaking Locatons</h1>
      </div>
      <br></br>
      <div className="location-content">
        {locations.map((location, index) => (
          <div className="location-card" key={index} onClick={() => handleImageClick(location.country)}>
            <img src={location.img} alt={location.country} className="location-image" />
            <div className="location-info">
              <h5>{location.country}</h5>
            </div>
          </div>
        ))}
      </div>
      {isDrawerOpen && selectedCountry && (
        <div className="drawer drawer-center small-size">
          <button className="close-drawer fixed-button" onClick={closeDrawer}>X</button>
          <h1 style={{ color: "black" }}>{selectedCountry}</h1>
          <div className="card-containernew">
            {destinations[selectedCountry]?.map((destination, index) => (
              <div className="flip-card" key={index}>
                <div className="flip-card-inner">
                  <div className="flip-card-front">
                    <h3>{destination.name}</h3>
                  </div>
                  <div className="flip-card-back">
                    <p>{destination.description}</p>
                    <br />
                    <h4>{destination.state}</h4>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </section>
  );
}

export default Locations;
